import { gql } from 'apollo-boost';


const getFacultyQuery = gql`
  {
    faculties{
      name
      id
      address
    }
  }
`


const getStudentQuery = gql`
  {
      students{
          name
          id
          age
      }
    }
`

// const addStudentMutation = gql`
