// `

const addFacultyMutation = gql`
   mutation($name:String!, $address:String!, $studentId:ID!)
   {
     addFaculty(name:$name ,address:$address, studentId:$studentId)
    {
      name
      address
      id
    }
   }

`
const getFacultysQuery = gql`
   query($id: ID)
   {faculty(id:$id)
   {
     name
     address
     students{
       id
       name
       age
        faculties{
          name
          id
          address
        }
     }
   }
   }
`

