import React, {Component} from 'react';
import { graphql } from 'react-apollo';
import {getFacultysQuery} from '../Queries/queries';


class FacultyDetails extends Component {


        displayFacultyDetails(){
            const { faculty } = this.props.data;
            if(faculty)
            {
                return(
                    <div>
                        <h2>{faculty.name}</h2>
                        <p>{faculty.address}</p>
                        <p>{faculty.students.name}</p>
                        <p>All students of this faculty </p><hr/>
                        <div>
                            {
                                faculty.students.faculties.map(item => {
                                    return <li key={item.id}>{item.name}</li>
                                })
                            }
                        </div>
                    </div>
                )
            }
            else{
                return (<div>No faculty selected...</div>)
            }
        }
        render(){
    return (
      <div >
       <ul id="facultyDetails">
           <li>
                {this.displayFacultyDetails()}
           </li>
      </ul>
      </div>
    );
  }
  }

  export default graphql(getFacultysQuery,{
    options:(props) =>{
      return {
        variables:{
          id:props.facultyid
        }
      }
    }
  })(FacultyDetails);
